import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { allUserRoute } from '../../utils/APIRoutes';
import Contacts from '../../components/Contacts';

function Chat() {
  const navigate = useNavigate();

  const [contacts, setContacts] = useState([]);
  const [currentUser, setCurrentUser] = useState(undefined);

  useEffect(() => {
    const fetchData = async () => {
      if (!localStorage.getItem("chat-app-user")) {
        navigate('/login');
      } else {
        const storedUser =await localStorage.getItem("chat-app-user");
        const parsedUser = await JSON.parse(storedUser);
      setCurrentUser(parsedUser);
       
      }
    };

    fetchData();
  }, [navigate]);

  useEffect(() => {
    const fetchData = async () => {
      if (currentUser && currentUser.isAvatarImageSet && currentUser._id) {
        try {
          const data = await axios.get(`${allUserRoute}/${currentUser._id}`);
          setContacts(data.data);
        } catch (error) {
          console.error('Error fetching user data:', error);
          // Handle the error, perhaps by redirecting to an error page or showing a message to the user.
        }
      } else {
        navigate('/setAvatar');
      }
    };
  
    fetchData();
  }, [currentUser, navigate]);


  return (
   
    <Container>
      <div className='container'>
        {console.log(currentUser)}
        <Contacts contacts={contacts} currentUser={currentUser} />
      </div>
    </Container>
  );
}

const Container = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;

  .container {
    height: 85vh;
    width: 85vw;
    background-color: #00000076;
    display: grid;
    grid-template-columns: 25% 75%;

    @media screen and (min-width: 720px) and (max-width: 1080px) {
      grid-template-columns: 35% 65%;
    }
  }
`;

export default Chat;
