import React from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import "./Register"


function Register() {

  const handleSubmit = (e)=>{
    e.preventDefault();
    alert('form')
  }
const handleChange = (e)=>{

} 

  return (
    <>
    <FormContainer>
      <form onSubmit={(e)=>handleSubmit(e)}>


<div className='brand'>
  <img src="" alt="" />
  <h1>Chat</h1>
</div>

<input 
type="text"
placeholder='username'
name='username'
onChange={(e)=>handleChange(e)}  />

<input 
type="email"
placeholder='Email'
name='email'
onChange={(e)=>handleChange(e)}  />

<input 
type="password"
placeholder='Password'
name='password'
onChange={(e)=>handleChange(e)}  />

<input 
type="password"
placeholder='Confirm Password'
name='confirmPassword'
onChange={(e)=>handleChange(e)}  />

<button type='submit'>Create Account</button>
<span>
  Already have an account ? <Link to={'/login'}>Login</Link>
</span>

      </form>
    </FormContainer>
    </>
  )
}

const FormContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  gap: 1rem;
  align-items: center;
  background-color: #131324;

  .brand {
    display: flex;
    align-items: center;
    gap: 1rem;
    justify-content: center;

    img {
      height: 5rem;
    }

    h1 {
      color: white;
      text-transform: uppercase;
    }
  }

  form {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    background-color: #00000076;
    border-radius: 2rem;
    padding: 3rem 5rem;
  }

  input {
    background-color: transparent;
    padding: 1rem;
    border: 0.1rem solid #4e0eff;
    border-radius: 0.4rem;
    color: white;
    width: 100%;
    font-size: 1rem;

    &:hover {
      background-color: #4e0eff;
      outline: none;
    }
  }

  button {
    background-color: #997af0;
    color: white;
    padding: 1rem 2rem;
    border: none;
    font-weight: bold;
    cursor: pointer;
    border-radius: 0.4rem;
    font-size: 1rem;
    text-transform: uppercase;
    transition: 0.5s ease-in-out;

    &:hover {
      background-color: #4e0eff;
    }
  }

  span {
    color: white;
    text-transform: none;

    a {
      color: #4e0eff;
      text-transform: none;
      font-weight: bold;
    }
  }
`;
export default Register
